import React ,{ useState,useEffect }from 'react';
import logo from '../img/logo.png';
import './Navbar.css';
import { Link } from 'react-router-dom';

export default function Navbar() {
 
  const [token, setToken] = useState(localStorage.getItem("jwt"));

  useEffect(() => {
    const handleStorageChange = () => {
      setToken(localStorage.getItem("jwt"));
    };

    window.addEventListener("storage", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange);
    };
  }, []);
  const loginStatus = () => {
    if (token) {
      return (
        <>
          <Link to="/Profile" className="nav-link"><li>Profile</li></Link>
          <Link to='/CreatePost' className="nav-link"><li>Create Post</li></Link>
        </>
      );
    } else {
      return (
        <>
          <Link to="/SignUp" className="nav-link"><li>SignUp</li></Link>
          <Link to="/SignIn" className="nav-link"><li>SignIn</li></Link>
        </>
      );
    }
  };

  return (
    <div className="Navbar">
      <img src={logo} alt='Logo' />
      <ul className="nav-menu">
        {loginStatus()}
      </ul>
    </div>
  );
}
