import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import './Home.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate('/SignUp');
    } else {
      fetch("http://localhost:5000/allposts", {
        headers: {
          "Authorization": "Bearer " + token
        },
      })
      .then(res => res.json())
      .then(result => {
        setData(result);
      })
      .catch(err => {
        console.log(err);
      });
    }
  }, [navigate]);

  const updatePost = (updatedPost) => {
    setData(data.map(post => post._id === updatedPost._id ? updatedPost : post));
  };

  const likePost = (id) => {
    fetch('http://localhost:5000/like', {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({
        postId: id
      })
    })
    .then(res => {
      if (!res.ok) {
        throw new Error('Network response was not ok ' + res.statusText);
      }
      return res.json();
    })
    .then(result => {
      updatePost(result);
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };

  const unlikePost = (id) => {
    fetch('http://localhost:5000/unlike', {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      },
      body: JSON.stringify({
        postId: id
      })
    })
    .then(res => {
      if (!res.ok) {
        throw new Error('Network response was not ok ' + res.statusText);
      }
      return res.json();
    })
    .then(result => {
      updatePost(result);
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };

  return (
    <div className="home">
      {data.map(post => (
        <div className="card" key={post._id}>
          <div className="card-header">
            <div className="card-pic">
              <img src={profile} alt="Profile" />
            </div>
            <h5>{post.postedBy ? post.postedBy.name : "Unknown User"}</h5>
          </div>
          <div className="card-image">
            <img src={post.photo} alt="Post" />
            <div className="card-content">
              <span
                className={`material-symbols-outlined ${post.likes.includes(localStorage.getItem("userId")) ? 'liked' : ''}`}
                onClick={() => post.likes.includes(localStorage.getItem("userId")) ? unlikePost(post._id) : likePost(post._id)}
              >
                favorite
              </span>
              <p>{post.likes.length} likes</p>
              <p>{post.body}</p>
              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => {
                  // Implement toggleComment function if needed
                  // toggleComment(post);  // Uncomment this if you have the function defined
                }}
              >
                View all comments
              </p>
            </div>
          </div>
          <div className="add-comment">
            <span className="material-symbols-outlined">mood</span>
            <input type="text" placeholder='Add a comment' />
            <button className="comment">Post</button>
          </div>
        </div>
      ))}
    </div>
  );
}
