import React, { useState, useEffect, useRef } from "react";
import './ProfilePic.css'
export default function ProfilePic({ changeprofile }) {
  const hiddenFileInput = useRef(null);
  const [image, setImage] = useState("");
  const [url, setUrl] = useState("");

  const postDetails = () => {
 
    console.log(body, image)
    const data = new FormData()
    data.append("file", image)
    data.append("upload_preset", "hasitha2")
    data.append("cloud_name", "hasitha2")
    fetch("https://api.cloudinary.com/v1_1/hasitha2/image/upload", {
      method: "post",
      body: data
    }).then(res => res.json())
      .then(data =>setUrl(data.url))
      .catch(err => console.log(err))
     
     
  }
  
  useEffect(() => {

    // saving post to mongodb
    if (url) {

      fetch("http://localhost:5000/profilePic", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt")
        },
        body: JSON.stringify({
          pic: url
        })
      }).then(res => res.json())
        .then(data => {
          if (data.error) {
            //notifyA(data.error)
            alert("add all fields");
            return
          } else {
            //notifyB("Successfully Posted")
         //  navigate("/home")
           changeprofile();
          alert("Post uploaded");
          }
        })
        .catch(err => console.log(err))
    }

  }, [url])
  // const postPic = () => {
  //   // saving post to mongodb
  //   fetch("http://localhost:5000/profilePic", {
  //     method: "put",
  //     headers: {
  //       "Content-Type": "application/json",
  //       Authorization: "Bearer " + localStorage.getItem("jwt"),
  //     },
  //     body: JSON.stringify({
  //       pic: url,
  //     }),
  //   })
  //     .then((res) => res.json())
  //     .then((data) => {
  //       console.log(data);
  //       changeprofile();
  //       window.location.reload();
  //     })
  //     .catch((err) => console.log(err));
  // };
  const handleClick = () => {
    hiddenFileInput.current.click();
  };

  
    return (
        <div className="profilePic darkBg">
          <div className="changePic centered">
            <div>
              <h2>Change Profile Photo</h2>
            </div>
            <div style={{ borderTop: "1px solid #00000030" }}>
              <button
                className="upload-btn"
                style={{ color: "#1EA1F7" }}
                onClick={handleClick}
              >
                Upload Photo
              </button>
              <input
                type="file"
                ref={hiddenFileInput}
                accept="image/*"
                style={{ display: "none" }}
                onChange={(e) => {
                  setImage(e.target.files[0]);
                }}
              />
            </div>
            <div style={{ borderTop: "1px solid #00000030" }}>
              <button className="upload-btn" style={{ color: "#ED4956" }}>
                {" "}
                Remove Current Photo
              </button>
            </div>
            <div style={{ borderTop: "1px solid #00000030" }}>
              <button
                style={{
                  background: "none",
                  border: "none",
                  cursor: "pointer",
                  fontSize: "15px",
                }}
                onClick={changeprofile}
              >
                cancel
              </button>
            </div>
          </div>
        </div>
      );
    }
