import React, { useRef, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './ProfilePic.css';

export default function ProfilePic({ changeprofile }) {
  const fileInputRef = useRef(null);
  const [image, setImage] = useState("");
  const [url, setUrl] = useState("");
  const [body, setBody] = useState(""); // This assumes you have some post content
  const navigate = useNavigate();

  const handleUploadClick = () => {
    fileInputRef.current.click();
  };

  const handleImageChange = (e) => {
    setImage(e.target.files[0]);
    postDetails(e.target.files[0]);
  };

  const postDetails = (image) => {
    const data = new FormData();
    data.append("file", image);
    data.append("upload_preset", "hasitha2");
    data.append("cloud_name", "hasitha2");

    fetch("https://api.cloudinary.com/v1_1/hasitha2/image/upload", {
      method: "post",
      body: data
    })
    .then(res => res.json())
    .then(data => {
      setUrl(data.url);
      console.log("Image URL:", data.url);
    })
    .catch(err => console.log(err));
  };

  useEffect(() => {
    if (url) {
      fetch("http://localhost:5000/CreatePost", {
        method: "post",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + localStorage.getItem("jwt")
        },
        body: JSON.stringify({
          body,
          pic: url
        })
      }).then(res => res.json())
        .then(data => {
          if (data.error) {
            alert("Add all fields");
            return;
          } else {
            navigate("/home");
            alert("Post uploaded");
          }
        })
        .catch(err => console.log(err));
    }
  }, [url, body, navigate]); // Include dependencies for body and navigate

  return (
    <div className='profilePic darkBg'>
      <div className="changePic centered">
        <div>
          <h2>Change profile pic</h2>
        </div>
        <div style={{ borderTop: "1px solid #00000030" }}>
          <button className='upload-btn' style={{ color: "#009566" }} onClick={handleUploadClick}>Upload photo</button>
          <input type='file' accept="image/*" style={{ display: "none" }} onChange={handleImageChange} ref={fileInputRef} />
        </div>
        <div style={{ borderTop: "1px solid #00000030" }}>
          <button className='upload-btn' style={{ color: "red" }}>Remove Current photo</button>
        </div>
        <div style={{ borderTop: "1px solid #00000030" }}>
          <button className='cancelBtn' onClick={changeprofile}>Cancel</button>
        </div>
      </div>
    </div>
  );
}
