import React, { useState, useEffect } from 'react';
import profile from '../img/profile.jpg';
import './Profile.css';
import { useNavigate } from 'react-router-dom';

export default function UserProfile({ userId }) {
  const [data, setData] = useState([]);
  const navigate = useNavigate();

  const notifyB = (message) => alert(message);

  const fetchUserPosts = async (userId) => {
    try {
      const response = await fetch(`http://localhost:5000/user/${userId}`, {
        headers: {
          "Authorization": "Bearer " + localStorage.getItem("jwt")
        }
      });

      const result = await response.json();

      console.log("Fetch result:", result); // Log the fetched result

      if (result.error) {
        notifyB(result.error);
      } else {
        console.log("Fetched posts:", result.posts); // Log the posts before setting state
        setData(result.posts);
      }
    } catch (err) {
      console.error("Fetch error:", err); // Log any fetch errors
      notifyB('Failed to fetch posts');
    }
  };

  useEffect(() => {
    if (userId) {
      fetchUserPosts(userId);
    }
  }, [userId]);

  useEffect(() => {
    if (data.length > 0) {
      console.log("Data state updated:", data); // Log the data state when it updates
      navigate(`/Profile/${userId}`); // Navigate after data is set
    }
  }, [data, navigate, userId]); 

   
  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img src={profile} alt="profile" />
        </div>
        <div className="profile-data">
          <h1>Hasithaa</h1>
          <div className="profile-info" style={{ display: 'flex', justifyContent: 'space-around' }}>
            <p>40 posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", margin: "auto", opacity: "0.8" }} />
      <div className="gallery">
        {data.length > 0 ? (
          data.map((post) => (
            <div key={post._id}>
              <img src={post.photo} alt="post" />
            </div>
          ))
        ) : (
          <p>No posts available</p>
        )}
      </div>
    </div>
  );
}
