import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import './Profile.css';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

export default function Profile() {
  const [data, setData] = useState([]);
  const navigate = useNavigate(); // Initialize navigate
  const notifyB = (message) => alert(message); // Replace this with your notification function

  useEffect(() => {
    fetch("http://localhost:5000/MyPosts", {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
      .then(res => res.json())
      .then((result) => setData(result));
  }, []); // Empty dependency array to run only once on mount

  const removePost = (postId) => {
    if (window.confirm("Do you really want to delete this post?")) {
      fetch(`http://localhost:5000/DeletePost/${postId}`, {
        method: "DELETE",
        headers: {
          "Authorization": "Bearer " + localStorage.getItem("jwt")
        }
      })
        .then((res) => res.json())
        .then((result) => {
          if (result.error) {
            notifyB(result.error);
          } else {
            console.log("Deleted successfully:", result);
            setData(data.filter(item => item._id !== postId)); // Update state to remove the deleted post
            notifyB("Post deleted successfully");
          }
        })
        .catch(err => {
          console.error("Error deleting post:", err);
          notifyB("Failed to delete post");
        });
    }
  };

  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img src={profile} alt="profile" />
        </div>
        <div className="profile-data">
          <h1>Hasithaa</h1>
          <div className="profile-info" style={{ display: 'flex', justifyContent: 'space-around' }}>
            <p>40 posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", margin: "auto", opacity: "0.8" }} />
      <div className="gallery">
        {data.map((pic) => (
          <div key={pic._id} className="post">
            <img src={pic.photo} alt="post" />
            {/* <button className="delete-button" onClick={() => removePost(pic._id)}>Delete</button> */}
          </div>
        ))}
      </div>
    </div>
  );
}
