import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import './Home.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate('/SignUp');
    } else {
      fetch("http://localhost:5000/allposts", {
        headers: {
          "Authorization": "Bearer " + token
        },
      })
      .then(res => res.json())
      .then(result => {
        setData(result);
      })
      .catch(err => {
        console.log(err);
      });
    }
  }, [navigate]);

  return (
    <div className="home">
      {data.map(post => (
        <div className="card" key={post._id}>
          <div className="card-header">
            <div className="card-pic">
              <img src={profile} alt="Profile" />
            </div>
            <h5>{post.postedBy ? post.postedBy.name : "Unknown User"}</h5>
          </div>
          <div className="card-image">
            <img src={post.photo} alt="Post" />
            <div className="card-content">
              <span className="material-symbols-outlined">favorite</span>
              
              <span className="material-symbols-outlined-red">favorite</span>
              
              <p>Likes</p>
              <p>{post.body}</p>
              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => {
                  // Implement toggleComment function if needed
                  // toggleComment(post);  // Uncomment this if you have the function defined
                }}
              >
                View all comments
              </p>
            </div>
          </div>
          <div className="add-comment">
            <span className="material-symbols-outlined">mood</span>
            <input type="text" placeholder='Add a comment' />
            <button className="comment">Post</button>
          </div>
        </div>
      ))}
    </div>
  );
}
