import React from 'react';
import logo from '../img/logo.png';
import './Navbar.css';
import { Link } from 'react-router-dom';

export default function Navbar({login}) {
  const token = localStorage.getItem("jwt");

  const loginStatus = () => {
    if (token || login) {
      return (
        <>
          <Link to="/Profile" className="nav-link"><li>Profile</li></Link>
          <Link to='/CreatePost' className="nav-link"><li>Create Post</li></Link>
        </>
      );
    } else {
      return (
        <>
          <Link to="/SignUp" className="nav-link"><li>SignUp</li></Link>
          <Link to="/SignIn" className="nav-link"><li>SignIn</li></Link>
        </>
      );
    }
  };

  return (
    <div className="Navbar">
      <img src={logo} alt='Logo' />
      <ul className="nav-menu">
        {loginStatus()}
      </ul>
    </div>
  );
}
