import React, { useState, useEffect } from 'react';
import profile from '../img/profile.jpg';
import './Profile.css';
import { useNavigate } from 'react-router-dom';
import Postdetails from './Postdetails';

export default function UserProfile({ userId }) {
  const [data, setData] = useState([]);
  const [show, setShow] = useState(false);
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  const notifyB = (message) => alert(message);

  const fetchUserPosts = (userId) => {
    fetch(`http://localhost:5000/user/${userId}`, {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
      .then(res => res.json())
      .then((result) => {
        if (result.error) {
          notifyB(result.error);
        } else {
          alert("Gettimg the user Profile")
          console.log(result);
          setData(result.posts);
        }
      })
      .catch(err => {
        console.error(err);
        notifyB('Failed to fetch posts');
      });
  };

  useEffect(() => {
    if (userId) {
      fetchUserPosts(userId);
    }
  }, [userId]);

  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img src={profile} alt="profile" />
        </div>
        <div className="profile-data">
          <h1>Hasithaa</h1>
          <div className="profile-info" style={{ display: 'flex', justifyContent: 'space-around' }}>
            <p>40 posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", margin: "auto", opacity: "0.8" }} />
      <div className="gallery">
        {data.map((pic) => (
          <div key={pic._id} className="post">
            <img 
              src={pic.photo} 
              alt="post" 
            />
          </div>
        ))}
      </div>
      {/* {show && <Postdetails item={posts} toggledetails={toggledetails} />} */}
    </div>
  );
}
