import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import "./Profile.css";
import { useNavigate } from 'react-router-dom';
import Postdetails from './Postdetails';
import { useParams } from 'react-router-dom';
export default function UserProfile() {
  const [data, setData] = useState([]);
  const {userid}=useParams();
  const [user,setUser]=useState("");
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  const notifyB = (message) => alert(message);

 const followUser =(userid)=>
 {
            fetch(`http://localhost:5000/follow`,{
              method:"put",
              headers: {
                "Content-Type":"application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
              },
              body:JSON.stringify({
                followId:userid,

              })
            }).then((res)=>{res.json()})
            .then((data)=>
            {
              console.log(data);
            //  setData(data)
            })
 }


 const UnfollowUser =(userid)=>
 {
            fetch(`http://localhost:5000/unfollow`,{
              method:"put",
              headers: {
                "Content-Type":"application/json",
                "Authorization": "Bearer " + localStorage.getItem("jwt")
              },
              body:JSON.stringify({
                followId:userid,

              })
            }).then((res)=>{res.json()})
            .then((data)=>
            {
              console.log(data);
             setData(data)
            })
 }
  useEffect(() => {
    fetch(`http://localhost:5000/user/${userid}`, {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
      .then(res => res.json())
      .then((result) => 
      { console.log("Results.......")
        console.log(result);
        setUser(result.user)
       setPosts(result.posts)
      }).catch(err => console.log(err));
  }, []);

  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img src={profile} alt="profile" />
        </div>
        <div className="profile-data">
          <div style={{display:"flex",alignItems:"center" }}>
          <h1>{user.name}</h1>
          <button className='followBtn' style={{ 
    // Your custom button styles here
    padding: '10px 20px', // Example padding
    borderRadius: '20px', // Example border radius
    border: 'none', // Remove default border
    backgroundColor: '#405DE6', // Instagram blue color
    color: 'white', // Text color
    boxShadow: '0 0 10px rgba(64, 93, 230, 0.5)', // Box shadow for glow effect
    cursor: 'pointer', // Cursor style
    transition: 'box-shadow 0.3s ease', // Smooth transition for glow effect
  }} onClick={()=>{followUser(user._id)}}>Follow</button>
          {/* <button className='followBtn'>Follow</button> */}
          </div>
          <div className="profile-info" style={{ display: 'flex', justifyContent: 'space-around' }}>
            <p>{posts.length} posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", margin: "auto", opacity: "0.8" }} />
      <div className="gallery">
        {posts.map((pic) => (
          <div key={pic._id} className="post">
            <img 
              src={pic.photo} 
              // onClick={() => toggledetails(pic)} 
              alt="post" 
            />
          </div>
        ))}
      </div>
      {/* {show && <Postdetails item={posts} toggledetails={toggledetails}  onDelete={deletePostFromState}/>} */}
    </div>
  );
}
