import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import './Home.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const navigate = useNavigate();
  const [data, setData] = useState([]);

  useEffect(() => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate('/SignUp');
    } else {
      fetch("http://localhost:5000/allposts", {
        headers: {
          "Authorization": "Bearer " + token
        },
      })
      .then(res => res.json())
      .then(result => {
        setData(result);
      })
      .catch(err => {
        console.log(err);
      });
    }
  }, [navigate]);

  const toggleLike = (postId) => {
    const token = localStorage.getItem("jwt");
    if (!token) {
      navigate('/SignUp');
    } else {
      const updatedData = data.map(post => {
        if (post._id === postId) {
          const isLiked = post.likes.includes(postId);
          const updatedLikes = isLiked ? post.likes.filter(id => id !== postId) : [...post.likes, postId];
          return { ...post, likes: updatedLikes };
        }
        return post;
      });
      setData(updatedData);

      const post = data.find(p => p._id === postId);
      const isLiked = post.likes.includes(postId);

      const emojiElement = document.getElementById(`emoji-${postId}`);
      emojiElement.style.color = isLiked ? 'red' : 'black'; // Change color based on like status

      const endpoint = isLiked ? 'unlike' : 'like';
      fetch(`http://localhost:5000/${endpoint}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          "Authorization": "Bearer " + token
        },
        body: JSON.stringify({
          postId: postId
        })
      })
      .then(res => {
        if (!res.ok) {
          throw new Error('Network response was not ok ' + res.statusText);
        }
        // Optionally, update the UI here
      })
      .catch(error => {
        console.error('There has been a problem with your fetch operation:', error);
      });
    }
  };
  
  return (
    <div className="home">
      {data.map(post => (
        <div className="card" key={post._id}>
          <div className="card-header">
            <div className="card-pic">
              <img src={profile} alt="Profile" />
            </div>
            <h5>{post.postedBy ? post.postedBy.name : "Unknown User"}</h5>
          </div>
          <div className="card-image">
            <img src={post.photo} alt="Post" />
            <div className="card-content">
              <span
                className={`material-symbols-outlined ${post.likes.includes(post._id) ? 'liked' : ''}`}
                onClick={() => toggleLike(post._id)}
              >
                <span id={`emoji-${post._id}`} style={{ color: post.likes.includes(post._id) ? 'red' : 'black' }}>❤️</span>
              </span>
              <p>{post.likes.length} likes</p>
              <p>{post.body}</p>
              <p
                style={{ fontWeight: "bold", cursor: "pointer" }}
                onClick={() => {
                  // Implement toggleComment function if needed
                  // toggleComment(post);  // Uncomment this if you have the function defined
                }}
              >
                View all comments
              </p>
            </div>
          </div>
          <div className="add-comment">
            <span className="material-symbols-outlined">mood</span>
            <input type="text" placeholder='Add a comment' />
            <button className="comment">Post</button>
          </div>
        </div>
      ))}
    </div>
  );
}
