import React, { useEffect, useState } from 'react';
import profile from '../img/profile.jpg';
import './Profile.css';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import Postdetails from './Postdetails';
export default function Profile() {
  const [data, setData] = useState([]);
  const [show,setShow]=useState(false);
  const [posts,setPosts]=useState([]);
  const navigate = useNavigate(); // Initialize navigate
  const notifyB = (message) => alert(message); // Replace this with your notification function
  const toggledetails=(posts)=>
   {
    if(show)
    {
   setShow(false)
    }
    else{
      setShow(true)
      setPosts(posts);
    }
  }
  useEffect(() => {
    fetch("http://localhost:5000/MyPosts", {
      headers: {
        "Authorization": "Bearer " + localStorage.getItem("jwt")
      }
    })
      .then(res => res.json())
      .then((result) => setData(result));
  }, []); // Empty dependency array to run only once on mount

  
  return (
    <div className="profile">
      <div className="profile-frame">
        <div className="profile-pic">
          <img src={profile} alt="profile" />
        </div>
        <div className="profile-data">
          <h1>Hasithaa</h1>
          <div className="profile-info" style={{ display: 'flex', justifyContent: 'space-around' }}>
            <p>40 posts</p>
            <p>40 followers</p>
            <p>40 following</p>
          </div>
        </div>
      </div>
      <hr style={{ width: "90%", margin: "auto", opacity: "0.8" }} />
      <div className="gallery">
      <div className="gallery">
  {data.map((pic) => (
    <div key={pic._id} className="post">
      <img 
        src={pic.photo} 
        onClick={() => toggledetails(pic)} 
        alt="post" 
      />
      {/* <button className="delete-button" onClick={() => removePost(pic._id)}>Delete</button> */}
    </div>
  ))}
</div>

      { show &&   <Postdetails item ={posts} toggledetails={toggledetails}/>}
      </div>
    </div>
  );
}
