import React from 'react'
import './Postdetails.css'
import profile from '../img/profile.jpg';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark } from '@fortawesome/free-solid-svg-icons';

import { faTrash } from '@fortawesome/free-solid-svg-icons';



export default function Profiledetails({item,toggledetails}) {

  const removePost = (postId) => {
    if (window.confirm("Do you really want to delete this post ?")) {
      fetch(`http://localhost:5000/DeletePost/${postId}`, {
        method: "delete",
        headers: {
          authorization: "Bearer " + localStorage.getItem("jwt"),
        },
      })
        .then((res) => res.json())
        .then((result) => {
          console.log(result);
          toggledetails();
        });
    }
  };

  return (
    <div className="showComment">
    <div className="container">
      <div className="postPic">
        <img src={item.photo}  alt="" />
      </div>
      <div className="details">
        {/* card header */}
        <div
          className="card-header"
          style={{ borderBottom: "1px solid #00000029" }}
        >
          <div className="card-pic">
            <img
              src={profile}  //here comes the image
              alt="hello"
            />
          </div>
          <h5>{item.postedBy.name}</h5>
          <div className="deletePost" onClick={()=>{ removePost();}}><FontAwesomeIcon icon={faTrash} />
          </div>
        </div>

        {/* commentSection */}
        <div
          className="comment-section"
          style={{ borderBottom: "1px solid #00000029" }}
        >
           {item.comments.map((it)=>
           {
             return ( <p className="comm">
             <span
               className="commenter"
               style={{ fontWeight: "bolder" }}
             >
             <p>{it.postedBy.name}</p>
             </span>
             <span className="commentText">{it.comment}</span>
           </p>)
           })}
             
            
          
        </div>

        {/* card content */}
        <div className="card-content">
          <p>{item.likes.length} Likes</p>
          <p>{item.body}</p>
        </div>

        {/* add Comment */}
        <div className="add-comment">
          <span className="material-symbols-outlined">mood</span>
          <input
            type="text"
            placeholder="Add a comment"
            // value={comment}
            // onChange={(e) => {
            //   setComment(e.target.value);
            // }}
          />
          <button
            className="comment"
            onClick={() => {

          ///    toggledetails();
            }}
          >
            Post
          </button>
          <div className="close-comment" onClick={()=>{ toggledetails();}}>
          <FontAwesomeIcon icon={faXmark} /></div>
        </div>
      </div>
    </div>
    </div>
  )
}
